document.getElementById("contactForm").addEventListener("submit",function(event){const email=document.getElementById("email").value;const honeypot=document.getElementById("company").value;if(honeypot){event.preventDefault();alert("Spam detected!")}const emailPattern=/^[^\s@]+@[^\s@]+\.[^\s@]+$/;if(!emailPattern.test(email)){event.preventDefault();alert("Please enter a valid email address.")}});document.addEventListener("DOMContentLoaded",function(){const hamburger=document.getElementById("hamburger");const mobileNav=document.getElementById("mobile-nav");const closeBtn=document.getElementById("close-btn");hamburger.addEventListener("click",function(){mobileNav.classList.add("active")});closeBtn.addEventListener("click",function(){mobileNav.classList.remove("active")})});