document.addEventListener("DOMContentLoaded",function(){const hamburger=document.getElementById("hamburger");const mobileNav=document.getElementById("mobile-nav");const closeBtn=document.getElementById("close-btn");if(hamburger&&mobileNav&&closeBtn){hamburger.addEventListener("click",function(){mobileNav.classList.add("active");hamburger.classList.add("hidden")});closeBtn.addEventListener("click",function(){mobileNav.classList.remove("active");hamburger.classList.remove("hidden")})}else{console.error("Hamburger navigation elements not found")}console.log("js is working")});