document.addEventListener("DOMContentLoaded",function(){const contactForm=document.getElementById("contactForm");if(contactForm){contactForm.addEventListener("submit",function(event){const email=document.getElementById("email").value;const honeypot=document.getElementById("company").value;if(honeypot){event.preventDefault();alert("Spam detected!")}const emailPattern=/^[^\s@]+@[^\s@]+\.[^\s@]+$/;if(!emailPattern.test(email)){event.preventDefault();alert("Please enter a valid email address.")}})}else{console.error("Contact form not found")}const hamburger=document.getElementById("hamburger");const mobileNav=document.getElementById("mobile-nav");const closeBtn=document.getElementById("close-btn");if(hamburger&&mobileNav&&closeBtn){hamburger.addEventListener("click",function(){mobileNav.classList.add("active");hamburger.classList.add("hidden")});closeBtn.addEventListener("click",function(){mobileNav.classList.remove("active");hamburger.classList.remove("hidden")})}else{console.error("Hamburger navigation elements not found")}function loadContent(url,elementId){fetch(url).then(response=>response.text()).then(data=>{document.getElementById(elementId).innerHTML=data}).catch(error=>console.error("Error loading file:",error))}loadContent("assets/inc/head.php","head-placeholder");loadContent("assets/inc/header.php","header-placeholder");loadContent("assets/inc/footer.php","footer-placeholder")});